from pymongo import MongoClient
import random
import os
import dotenv
dotenv.load_dotenv()

connection = MongoClient(os.environ.get('CONNECTION_STRING'))[os.environ.get('DATABASE_NAME')]

dummy_cards = [
    {
        "type": "question" if random.randint(0, 1) == 0 else "activities",
        "gender": ["Male", "Female"],
        "description": dummy
    } for dummy in lipsum.split(". ")
]

connection['cards'].insert_many(dummy_cards)

print("Migrating Cards Complete!")